import MyNavbar from './components/MyNavbar.vue';
import axios from 'axios';
export default {
  components: {
    MyNavbar
  },
  data() {
    return {
      baseURL: "http://localhost:8080",
      products: [],
      categories: []
    };
  },
  methods: {
    async fetchData() {
      // api call to get all the categories
      await axios.get(`${this.baseURL}/category/list/`).then(res => this.categories = res.data).catch(err => console.log(err));

      //api call to get product
      await axios.get(`${this.baseURL}/product/`).then(res => this.products = res.data).catch(err => console.log(err));
    }
  },
  mounted() {
    this.fetchData();
  }
};
